import { Container, ImageList } from '@mui/material'
import React from 'react'
import AppBar from '@mui/material/AppBar';
import {Box, Grid} from '@mui/material';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Item from './Reusecomponets/Item';
import ShopCard from './Reusecomponets/ShopCard'
import Image from './products/876661122392077-2-product.webp'
import ProductsList from './ProductsList.json'
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { fullWidth } from 'validator/lib/isFullWidth';

import ImageListItem from '@mui/material/ImageListItem';

function Easyshop() {


  return (

    <Container maxWidth="100vw" sx={{backgroundColor:"#00000099", height:"auto",}}>
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" style={{backgroundColor:"#000"}}>
        <Toolbar>
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2 }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            News
          </Typography>
         <IconButton>
                <ShoppingCartIcon style={{color:"#FFF"}}/>
         </IconButton>
        </Toolbar>
      </AppBar>
    </Box>
    <Container maxWidth='xl' sx={{ height:"auto", marginTop:"2%"}}>
    <Box sx={{ width: '100%' }}>

      <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
        {
             ProductsList.map((ProductsDetails)=>{

                return(
        <Grid item xs={3}>
          <Item> 
          <Card sx={{ maxWidth: fullWidth }}>
      {/* <CardMedia
        sx={{ height: 300 }}
        image={ProductsDetails.path}
        title="green iguana"
      /> */}
      <img src={Image} />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {ProductsDetails.path}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Lizards are a widespread group of squamate reptiles, with over 6,000
          species, ranging across all continents except Antarctica
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small">Share</Button>
        <Button size="small">Learn More</Button>
      </CardActions>
    </Card> 
          </Item>
        </Grid>
          )    
        })
        }
      </Grid>


    </Box>
    </Container>
    <ImageList sx={{ width: 500, height: 450 }} cols={3} rowHeight={164}>
  {ProductsList.map((item) => (
    <ImageListItem key={item.path}>
      <img
        srcSet={`${Image}?w=164&h=164&fit=crop&auto=format&dpr=2 2x`}
        src={`${Image}?w=164&h=164&fit=crop&auto=format`}
        alt={item.title}
        loading="lazy"
      />
    </ImageListItem>
  ))}
</ImageList>

    </Container>

  )
}

export default Easyshop
